#include <iostream>
#include <string>
#include <unordered_map>
#include <vector>
#ifdef LOCAL
#include "stloutput.h"
#endif
#define INF 1000000000

using namespace std;

bool dfs(int x, vector<int> &path, vector<vector<int>> &g, vector<int> &ref,
         int N) {
    if (path.size() == N) return true;
    for (int i = 0; i < N; i++) {
        if (g[x][i]) ref[i]--;
    }
    for (int i = 0; i < N; i++) {
        if (ref[i] == 0) {
            path.push_back(i);
            ref[i]--;
            if (dfs(i, path, g, ref, N)) return true;
            ref[i]++;
            path.pop_back();
        }
    }
    for (int i = 0; i < N; i++) {
        if (g[x][i]) ref[i]++;
    }
    return false;
}

int main() {
    int N, kase = 1;
    while (cin >> N) {
        vector<string> vs;
        unordered_map<string, int> um;
        vector<vector<int>> graph(N, vector<int>(N));
        vector<int> ref(N);
        for (int i = 0; i < N; i++) {
            string s;
            cin >> s;
            um[s] = i;
            vs.push_back(s);
        }
        int M;
        cin >> M;
        for (int i = 0; i < M; i++) {
            string a, b;
            cin >> a >> b;
            if (!graph[um[a]][um[b]]) {
                graph[um[a]][um[b]] = 1;
                ref[um[b]]++;
            }
        }

        vector<int> path;
        for (int i = 0; i < N; i++) {
            if (ref[i] == 0) {
                ref[i]--;
                path.push_back(i);
                dfs(i, path, graph, ref, N);
                break;
            }
        }

        cout << "Case #" << kase++
             << ": Dilbert should drink beverages in this order:";
        for (auto i : path) {
            cout << " " << vs[i];
        }
        cout << '.' << endl << endl;
    }
}
