def dfs(x, graph, path, ref, ans, N):
    if ref[x] == -1:
        return

    ref[x] -= 1
    path.append(x)

    if len(path) == N:
        ans.append(list(map(lambda x: chr(x + ord('A')), path)))
        ref[x] += 1
        path.pop()
        return

    for idx, item in enumerate(graph[x]):
        if item:
            ref[idx] -= 1

    for idx, item in enumerate(ref):
        if item == 0:
            dfs(idx, graph, path, ref, ans, N)

    for idx, item in enumerate(graph[x]):
        if item:
            ref[idx] += 1

    ref[x] += 1
    path.pop()


def main():
    T = int(input())
    for t in range(T):
        input()
        varss = list(map(lambda x: ord(x) - ord('A'), input().split()))
        graph = [[0] * 26 for _ in range(26)]
        ref = [-1] * 26
        con = input().split()

        for i in varss:
            ref[i] = 0

        for i in con:
            a = ord(i[0]) - ord('A')
            b = ord(i[2]) - ord('A')
            graph[a][b] = 1
            ref[b] += 1

        ans = []
        for idx, item in enumerate(ref):
            if item == 0:
                dfs(idx, graph, [], ref, ans, len(varss))

        if t != 0:
            print('')

        if ans:
            for i in ans:
                print(*i)
        else:
            print("NO")


if __name__ == "__main__":
    main()
