void dfs(int x, int y, vector<string> &g) {
    if (g[x][y] == '.') return;
    g[x][y] = '.';
    dfs(x + 1, y, g);
    dfs(x, y + 1, g);
    dfs(x, y - 1, g);
    dfs(x - 1, y, g);
}

int main() {
    int T;
    cin >> T;
    for (int t = 1; t <= T; t++) {
        int N;
        cin >> N;
