        g.push_back(string(N + 2, '.'));
        int cnt = 0;
        for (int i = 1; i <= N; i++) {
            for (int j = 1; j <= N; j++) {
                if (g[i][j] == 'x') {
                    cnt++;
                    dfs(i, j, g);
                }
            }
        }
        cout << "Case " << t << ": " << cnt << endl;
    }
}
