using namespace std;

int main() {
    cin.sync_with_stdio(0);
    cin.tie(0);
    int T;
    cin >> T;
    for (int t = 1; t <= T; t++) {
        int N;
        cin >> N;
        vector<int> adj(N, -1);
        for (int i = 0; i < N; i++) {
            int u, v;
            cin >> u >> v;
            u--, v--;
            adj[u] = v;
        }
        int Max = 0, Maxp = 0;
        vector<bool> best_v(N);
        for (int i = 0; i < N; i++) {
            if (best_v[i]) continue;
            vector<bool> visited(N);
            vector<int> st;
            st.push_back(i);
            int cnt = 0;
            visited[i] = true;
            best_v[i] = true;
            while (!st.empty()) {
                int tmp = st.back();
                st.pop_back();
                if (!visited[adj[tmp]]) {
                    cnt++;
                    visited[adj[tmp]] = true;
                    best_v[adj[tmp]] = true;
                    st.push_back(adj[tmp]);
                }
            }
            if (cnt > Max) {
                Max = cnt;
                Maxp = i;
            }
        }
        cout << "Case " << t << ": " << Maxp + 1 << endl;
    }
}
