#include <iostream>
#include <set>
#include <utility>
#include <vector>
#define INF 1000000000

using namespace std;

void dfs(int x, int y, vector<int> &dx, vector<int> &dy, int R, int C,
         vector<vector<bool>> &g, vector<vector<bool>> &visited, int &even,
         int &odd) {
    if (visited[x][y]) return;
    visited[x][y] = true;
    int cnt = 0;
    set<pair<int, int>> se;
    for (int i = 0; i < 4; i++) {
        if (x + dx[i] < R && x + dx[i] >= 0 && y + dy[i] < C &&
            y + dy[i] >= 0 && !g[x + dx[i]][y + dy[i]]) {
            if (se.count(make_pair(x + dx[i], y + dy[i])) == 0) {
                cnt++;
                se.insert(make_pair(x + dx[i], y + dy[i]));
            }
            dfs(x + dx[i], y + dy[i], dx, dy, R, C, g, visited, even, odd);
        }
    }
    for (int i = 0; i < 4; i++) {
        if (x + dy[i] < R && x + dy[i] >= 0 && y + dx[i] < C &&
            y + dx[i] >= 0 && !g[x + dy[i]][y + dx[i]]) {
            if (se.count(make_pair(x + dy[i], y + dx[i])) == 0) {
                cnt++;
                se.insert(make_pair(x + dy[i], y + dx[i]));
            }
            dfs(x + dy[i], y + dx[i], dy, dx, R, C, g, visited, even, odd);
        }
    }
    cnt % 2 ? odd++ : even++;
}

int main() {
    int T;
    cin >> T;
    for (int t = 1; t <= T; t++) {
        int R, C, M, N, W;
        cin >> R >> C >> M >> N;
        vector<vector<bool>> g(R, vector<bool>(C));
        vector<vector<bool>> visited(R, vector<bool>(C));
        vector<int> dx = {1, 1, -1, -1}, dy = {1, -1, 1, -1};
        for (int i = 0; i < 4; i++) dx[i] *= M, dy[i] *= N;
        cin >> W;
        for (int i = 0; i < W; i++) {
            int x, y;
            cin >> x >> y;
            g[x][y] = true;
        }
        int even = 0, odd = 0;
        dfs(0, 0, dx, dy, R, C, g, visited, even, odd);
        cout << "Case " << t << ": " << even << ' ' << odd << endl;
    }
}
```

