#include <cctype>
#include <iostream>
#include <queue>
#include <vector>
#define INF 1000000000

using namespace std;

int chr2int(char c) {
    if (isupper(c)) {
        return c - 'A';
    } else {
        return c - 'a' + 26;
    }
}

class node {
   public:
    vector<int> nxt;
    int fail, str_idx, hit;
    node() {
        nxt.resize(52, -1);
        fail = hit = -1;
        str_idx = -1;
    }
};

void build_trie(string s, int str_idx, vector<node> &trie, int &node_cnt,
                vector<int> &equv) {
    int cur = 0;
    for (char i : s) {
        if (trie[cur].nxt[chr2int(i)] == -1) {
            trie[cur].nxt[chr2int(i)] = ++node_cnt;
        }
        cur = trie[cur].nxt[chr2int(i)];
    }
    if (trie[cur].str_idx != -1) {
        equv[str_idx] = trie[cur].str_idx;
    } else {
        trie[cur].str_idx = str_idx;
    }
}

void build_ac(vector<node> &trie) {
    queue<int> q;
    q.push(0);
    while (!q.empty()) {
        int p = q.front();
        q.pop();
        for (int i = 0; i < 52; i++) {
            if (trie[p].nxt[i] != -1) {
                int bk = trie[p].fail;
                while (bk != -1 && trie[bk].nxt[i] == -1) bk = trie[bk].fail;
                if (bk != -1) {
                    trie[trie[p].nxt[i]].fail = trie[bk].nxt[i];
                } else {
                    trie[trie[p].nxt[i]].fail = 0;
                }
                int r = trie[trie[p].nxt[i]].fail;
                if (trie[r].str_idx != -1) {
                    trie[trie[p].nxt[i]].hit = r;
                } else {
                    trie[trie[p].nxt[i]].hit = trie[r].hit;
                }
                q.push(trie[p].nxt[i]);
            }
        }
    }
}

void match(string s, vector<node> &trie, vector<bool> &found) {
    int p = 0;
    for (char i : s) {
        while (p != -1 && trie[p].nxt[chr2int(i)] == -1) p = trie[p].fail;
        if (p != -1) {
            p = trie[p].nxt[chr2int(i)];
        }
        for (int bk = p; bk != -1; bk = trie[bk].hit) {
            if (trie[bk].str_idx != -1) {
                found[trie[bk].str_idx] = true;
            }
        }
        if (p == -1) p = 0;
    }
}
int main() {
    cin.tie(0);
    cin.sync_with_stdio(0);
    int T;
    cin >> T;
    while (T--) {
        int node_cnt = 0, q;
        string s;
        vector<node> trie;
        trie.resize(1000005);
        cin >> s >> q;
        vector<int> equv(q, -1);
        for (int i = 0; i < q; i++) {
            string tmp;
            cin >> tmp;
            build_trie(tmp, i, trie, node_cnt, equv);
        }
        build_ac(trie);
        vector<bool> found(q);
        match(s, trie, found);
        for (int i = 0; i < q; i++) {
            if (found[i] || (equv[i] != -1 && found[equv[i]])) {
                cout << "y\n";
            } else {
                cout << "n\n";
            }
        }
    }
}

