#include <bits/stdc++.h>

using namespace std;

bool primeq(long long int x, vector<bool> &isprime, vector<int> &prime){
    if (x < 40000){
        if (isprime[x]) return true;
        else return false;
    }else{
        for (int j = 0; j < prime.size() && x > prime[j]*prime[j]; j++){
            if (x % prime[j] == 0){
                return false;
            }
        }
        return true;
    }
}

int digadd(int x){
    int sum=0;
    while (x){
        sum+=x%10;
        x/=10;
    }
    return sum;
}

int main(){
    vector<bool> isprime(40000,true);
    for (int i = 2; i < 40000; i++){
        if (isprime[i]){
            for (int j = i*i; j < 40000; j+=i){
                isprime[j] = false;
            }
        }
    }

    vector<int> prime;
    for (int i =2; i < 40000;i++){
        if (isprime[i]) prime.push_back(i);
    }


    int T;
    cin>>T;
    while(T--){
        long long n;
        cin>>n;
        for (int i = n+1;;i++){
            if (!primeq(i,isprime,prime)){
                int a = digadd(i),b = 0, x = i;
                for (int j = 0; j < prime.size();j++){
                    while (x%prime[j] == 0) {
                        b+=digadd(prime[j]);
                        x/=prime[j];
                    }
                }
                if (x!=1) b+= digadd(x);
                if(a == b){
                    cout<<i<<endl;
                    break;
                }
            }
        }
    }
}
