#include <cmath>
#include <iomanip>
#include <iostream>
#include <vector>
#define INF 1000000000

using namespace std;

double tsp(int x, int msk, vector<vector<double>> &graph, vector<int> &to_idx,
           vector<double> &val, vector<vector<double>> &dp, int P) {
    if (dp[x][msk] > -900000000) return dp[x][msk];
    if (msk == (1 << P) - 1) return dp[x][msk] = -graph[0][to_idx[x]];
    for (int i = 0; i < P; i++) {
        if (!(msk & (1 << i))) {
            dp[x][msk] =
                max(dp[x][msk],
                    max(-graph[0][to_idx[x]],
                        val[i] - graph[to_idx[i]][to_idx[x]] +
                            tsp(i, msk | (1 << i), graph, to_idx, val, dp, P)));
        }
    }

    return dp[x][msk];
}

int main() {
    int T;
    cin >> T;
    while (T--) {
        int N, M;
        cin >> N >> M;
        N++;
        vector<vector<double>> graph(N, vector<double>(N, INF));
        for (int i = 0; i < M; i++) {
            int a, b;
            double c;
            cin >> a >> b >> c;
            graph[a][b] = min(c, graph[a][b]);
            graph[b][a] = min(c, graph[b][a]);
        }

        for (int i = 0; i < N; i++)
            for (int s = 0; s < N; s++)
                for (int t = 0; t < N; t++)
                    graph[s][t] = min(graph[s][t], graph[s][i] + graph[i][t]);

        int P;
        cin >> P;
        P++;
        vector<int> to_idx;
        vector<double> val;
        to_idx.push_back(0);
        val.push_back(0);
        for (int i = 1; i < P; i++) {
            int a;
            double b;
            cin >> a >> b;
            to_idx.push_back(a);
            val.push_back(b);
        }
        vector<vector<double>> dp(P, vector<double>(1 << P, -INF));
        double ans = tsp(0, 1, graph, to_idx, val, dp, P);
        ans > 1E-9 ? cout << fixed << setprecision(2) << "Daniel can save $"
                          << ans << endl
                   : cout << "Don't leave the house" << endl;
    }
}

```

