#include <cmath>
#include <iostream>
#include <utility>
#include <vector>
#define INF 1000000000

using namespace std;

inline int dist(pair<int, int> a, pair<int, int> b) {
    return abs(a.first - b.first) + abs(a.second - b.second);
}

int tsp(int x, int msk, vector<vector<int>>& dp, vector<pair<int, int>>& vp) {
    if (dp[x][msk] != INF) return dp[x][msk];
    if (msk == (1 << vp.size()) - 1) return dist(vp[0], vp[x]);
