            dp[x][msk] = min(dp[x][msk], dist(vp[x], vp[i]) +
                                             tsp(i, msk | (1 << i), dp, vp));
        }
    }
    return dp[x][msk];
}

int main() {
    int T;
    cin >> T;
    while (T--) {
        int xs, ys, t1, t2, n;
        vector<pair<int, int>> vp;
        cin >> xs >> ys >> t1 >> t2;
