#include <cmath>
#include <iostream>
#include <vector>
#define INF 1000000000

using namespace std;

inline double dis(pair<int, int> a, pair<int, int> b) {
    return sqrt((a.first - b.first) * (a.first - b.first) +
                (a.second - b.second) * (a.second - b.second)) +
           16;
}

double magic(int x, int msk, vector<vector<double>> &dp,
             vector<pair<int, int>> &vp, vector<vector<int>> &from) {
    if (msk == (1 << vp.size()) - 1) return dp[x][msk] = 0;
    if (dp[x][msk] != INF) return dp[x][msk];
    double Min = INF;
    int min_to;
    for (int k = 0; k < vp.size(); k++) {
        if (k != x && !(msk & (1 << k))) {
            double tmp =
                magic(k, (1 << k) | msk, dp, vp, from) + dis(vp[k], vp[x]);
            if (Min > tmp) {
                Min = tmp;
                min_to = k;
            }
        }
    }
    from[x][msk] = min_to;
    return dp[x][msk] = Min;
}

int main() {
    int N, cnt = 1;
    while (cin >> N, N) {
        vector<pair<int, int>> vp;
        int t1, t2;
        for (int i = 0; i < N; i++) {
            cin >> t1 >> t2;
            vp.push_back(make_pair(t1, t2));
        }
        vector<vector<double>> dp(N, vector<double>(1 << N, INF));
        vector<vector<int>> from(N, vector<int>(1 << N, INF));

        for (int i = 0; i < N; i++) {
            dp[i][(1 << N) - 1] = 0;
        }

        int min_end;
        double Min = INF;
        for (int i = 0; i < N; i++) {
            double tmp = magic(i, 1 << i, dp, vp, from);
            if (Min > tmp) {
                Min = tmp;
                min_end = i;
            }
        }

        puts("**********************************************************");
        printf("Network #%d\n", cnt++);
        int msk = 1 << min_end;
        int cur = min_end, lst = min_end;
        for (int i = 0; i < N; i++) {
            if (cur != lst)
                printf(
                    "Cable requirement to connect (%d,%d) to (%d,%d) is %.2lf "
                    "feet.\n",
                    vp[lst].first, vp[lst].second, vp[cur].first,
                    vp[cur].second, dis(vp[cur], vp[lst]));
            lst = cur;
            msk |= (1 << cur);
            cur = from[cur][msk];
        }
        printf("Number of feet of cable required is %.2lf.\n",
               dp[min_end][(1 << min_end)]);
    }
}

