        for (int i = 0; i < coins.size(); i++) {
            for (int j = coins[i]; j <= n; j++) {
                dp[j] += dp[j - coins[i]];
            }
        }
        if (dp[n] == 1) {
            cout << "There is only 1 way to produce " << n << " cents change."
                 << endl;
        } else {
            cout << "There are " << dp[n] << " ways to produce " << n
                 << " cents change." << endl;
        }
    }
}
