class hand {  //æ‰‹ç‰Œé¡åˆ¥
   private:
    vector<vector<card>> h;  //æ‰‹ç‰Œ
    bool flush, straight;    //åŒèŠ±ã€é †å­
    int win;                 //ç‰Œç¨®é¡

   public:
    hand() { win = -1; };  //é è¨­-1
    hand(vector<card> v) {
        flush = straight = true;  //ç•¶ä½œéƒ½æ˜¯åŒèŠ±é †
        //ç”±å¤§åˆ°å°æ’åºæ‰‹ç‰Œ
        sort(begin(v), end(v), [](card& a, card& b) { return a > b; });
        //åˆ¤æ–·Aè¦ç•¶ä½œ1çš„ç‰¹ä¾‹
        if (v[0].num == 14 && v[1].num == 5 && v[2].num == 4 && v[3].num == 3 &&
            v[4].num == 2) {
            v[0].num = 1;
            sort(begin(v), end(v), [](card& a, card& b) { return a > b; });
        }
        int last_suit = v[0].suit,
            last_num = v[0].num + 1;  //ç´€éŒ„ä¸Šä¸€å€‹èŠ±è‰²ã€æ•¸å­—
        for (auto& i : v) {
            //èŠ±è‰²èˆ‡ä¸Šæ¬¡ä¸åŒéåŒèŠ±
            if (last_suit != i.suit) flush = false;
            //æ•¸å­—éä¸Šæ¬¡çš„-1 éé †å­
            if (last_num != i.num + 1) straight = false;
            last_suit = i.suit, last_num = i.num;
            bool pushed = false;
            //æŠŠæ•¸å­—ç›¸åŒçš„ç–Šåœ¨åŒä¸€æ¨
            for (auto& j : h) {
                if (i.num == j[0].num) {
                    j.push_back(i);
                    pushed = true;
                    break;
                }
            }
            //è·ŸåŸæœ¬çš„éƒ½ä¸ä¸€æ¨£åšä¸€å€‹æ–°çš„å †
            if (!pushed) {
                vector<card> tmp;
                tmp.push_back(i);
                h.push_back(tmp);
            }
        }
        //ç”¨æ¯å †çš„å¼µæ•¸stable_sort ä¿æŒåŸå…ˆæ’å¤§åˆ°å°çš„é †åº
        stable_sort(begin(h), end(h), [](vector<card> a, vector<card> b) {
            return a.size() > b.size();
        });
        //å°‹æ‰¾æœ€å¤§æ’å‹
        win = 0;                                            //éæ´²äººç‰Œé‹
        if (h[0].size() == 2) win = 1;                      //å°å­
        if (h[0].size() == 2 && h[1].size() == 2) win = 2;  //å…©å°
        if (h[0].size() == 3) win = 3;                      //ä¸‰æ¢
        if (straight) win = 4;                              //é †å­
        if (flush) win = 5;                                 //åŒèŠ±
        if (h[0].size() == 3 && h[1].size() == 2) win = 6;  //è‘«è˜†
        if (h[0].size() == 4) win = 7;                      //éµæ”¯
        if (flush && straight) win = 8;                     //ç‰›Bç‰Œ
    }
    int cmp(const hand& other) {  //åˆ¤æ–·å…©æ‰‹ç‰Œå¤§å° ä¸è€ƒæ…®èŠ±è‰² ç›¸åŒå›å‚³2
        if (win == other.win) {
            for (int i = 0; i < h.size(); i++) {
                for (int j = 0; j < h[i].size(); j++) {
                    if (h[i][j].num != other.h[i][j].num)
                        return h[i][j].num > other.h[i][j].num;
                }
            }
            return 2;
        } else {
            return win > other.win;
        }
    }
    bool operator>(const hand& other) {  //åˆ¤æ–·å…©æ‰‹ç‰Œå¤§å° è€ƒæ…®èŠ±è‰²
        if (win == other.win) {
            for (int i = 0; i < h.size(); i++) {
                for (int j = 0; j < h[i].size(); j++) {
                    if (h[i][j] != other.h[i][j])
                        return h[i][j] > other.h[i][j];
                }
            }
        } else {
            return win > other.win;
        }
    }
    friend ostream& operator<<(ostream& os, const hand& hd);
};

