#include <iostream>
#include <vector>
#define INF 1000000000

using namespace std;

int main() {
    cin.sync_with_stdio(0);
    cin.tie(0);
    int T;
    cin >> T;
    for (int t = 1; t <= T; t++) {
        int N, M, K;
        cin >> N >> M >> K;
        vector<vector<int>> graph(N, vector<int>(M));
        for (int i = 0; i < N; i++)
            for (int j = 0; j < M; j++) cin >> graph[i][j];
        int ans1 = 0, ans2 = 0;
        for (int st = 0; st < N; st++) {
            vector<int> dp(M);
            for (int ed = st; ed < N; ed++) {
                for (int i = 0; i < M; i++) dp[i] += graph[ed][i];
                int tmp = 0, stc = 0;
                for (int edc = 0; edc < M; edc++) {
                    tmp += dp[edc];
                    while (tmp > K) tmp -= dp[stc++];
                    int area = (edc - stc + 1) * (ed - st + 1);
                    if (area > ans1 || (area == ans1 && tmp < ans2)) {
                        ans1 = area;
                        ans2 = tmp;
                    }
                }
            }
        }
        cout << "Case #" << t << ": " << ans1 << " " << ans2 << '\n';
    }
}
