using namespace std;

long long int solve2d(vector<vector<long long>> &graph, int b, int c) {
    vector<long long> dp;
    long long int ans = -INF;
    for (int st = 0; st < b; st++) {
        dp.clear();
        dp.resize(c, 0);
        for (int ed = st; ed < b; ed++) {
            for (int i = 0; i < c; i++) {
                dp[i] = dp[i] + graph[ed][i];
            }
            long long int tmp = 0;
            for (int i = 0; i < c; i++) {
                tmp += dp[i];
                if (tmp > ans) ans = tmp;
                if (tmp < 0) tmp = 0;
            }
        }
    }
    return ans;
}

