    int T;
    cin >> T;
    while (T--) {
        int a, b, c;
        cin >> a >> b >> c;
        vector<vector<vector<long long>>> graph(
            a, vector<vector<long long>>(b, vector<long long>(c)));
        for (int i = 0; i < a; i++)
            for (int j = 0; j < b; j++)
                for (int k = 0; k < c; k++) cin >> graph[i][j][k];

        vector<vector<long long>> dp2;
        long long int ans = -INF;
        for (int st = 0; st < a; st++) {
            dp2.clear();
            dp2.resize(b, vector<long long>(c));
            for (int ed = st; ed < a; ed++) {
                for (int i = 0; i < b; i++) {
                    for (int j = 0; j < c; j++) {
                        dp2[i][j] += graph[ed][i][j];
