int ans, ansp;

void dfs(int x, int pos, int n, int a) {
    if (__builtin_popcount(pos) == a) {
        int sum = 0, tmp = pos;
        while (tmp != 0) {
            int tmp2 = tmp & (-tmp);
            tmp &= (~tmp2);
            sum += zone[tmp2];
        }
        for (auto &&i : over) {
            tmp = pos & i.first;
            if (tmp) {
                sum -= i.second * (__builtin_popcount(tmp) - 1);
            }
        }
        if (sum > ans) {
            ans = sum;
            ansp = pos;
        }
        return;
    }
    for (int i = (x == 0) ? 1 : (x << 1); i <= (1 << (n - 1)); i <<= 1)
        dfs(i, pos | i, n, a);
}

int main() {
