def prime():
    isprime = [True] * 32
    for i in range(2, 32):
        if isprime[i]:
            for j in range(i + i, 32, i):
                isprime[j] = False
    return isprime


def dfs(cur, ans, last, depth, used, n, isprime):
    if depth == n:
        if isprime[cur[-1] + cur[0]]:
            ans.append(list(cur))
        return
    for i in range(1, n + 1):
        if not used[i] and isprime[last + i]:
            used[i] = True
            cur.append(i)
            dfs(cur, ans, i, depth + 1, used, n, isprime)
            cur.pop()
            used[i] = False
    return ans


def main():
    T = 1
    isprime = prime()
    while True:
        try:
            n = int(input())
        except EOFError:
            break
        if T != 1:
            print('')
        print("Case {}:".format(T))
        for i in dfs([1], [], 1, 1, [True, True] + [False] * (n - 1), n, isprime):
            for j in i[:-1]:
                print(j, end=' ')
            print(i[-1])
        T += 1


if __name__ == "__main__":
    main()

