        comb.push_back(v);
        return;
    }
    for (int i = x + 1; i < 25 - (5 - depth) + 1; i++) {
        v.push_back(i);
        preprocess(i, depth + 1, v, comb);
        v.pop_back();
    }
}

int main() {
    ios::sync_with_stdio(0);
    vector<int> v;
    vector<vector<int>> comb;
    preprocess(-1, 0, v, comb);
    int T;
    cin >> T;
    while (T--) {
        vector<vector<int>> graph(5, vector<int>(5));
        vector<pair<int, int>> data;
        int N;
        cin >> N;
        while (N--) {
            int a, b, c;
            cin >> a >> b >> c;
            graph[a][b] = c;
            data.push_back(make_pair(a, b));
        }

        int Min = INF;
        vector<int> ans;
        for (auto &&i : comb) {
            int Sum = 0;
            for (auto &&k : data) {
                int tempMin = INF;
                for (auto &&j : i) {
                    int x = j / 5;
                    int y = j % 5;
                    tempMin =
                        min(tempMin, abs(k.first - x) + abs(k.second - y));
                }
                tempMin *= graph[k.first][k.second];
                Sum += tempMin;
            }
            if (Sum < Min) {
                ans = i;
                Min = Sum;
            }
        }

        cout << ans[0];
        for (int i = 1; i < ans.size(); i++)
            cout << ' ' << ans[i];
        cout << endl;
    }
}

