#include <iostream>
#include <vector>
#define INF 1000000000
using namespace std;

class segtree {
private:
	struct node {
		int neg = 0, zero = 0;

	};
	vector<node> v;
	vector<int>* data;
public:
	segtree(vector<int>* x) {
		v.resize(1000000);
		data = x;
		build(0, 0, data->size() - 1);
	}
	void build (int x, int L, int R) {
		if (L == R) {
			if (data->at(L) < 0)
				v[x].neg = 1;
			else if (data->at(L) == 0)
				v[x].zero = 1;
			return;
		}
		build (2 * x + 1, L, (L + R) / 2);
		build (2 * x + 2, (L + R) / 2 + 1, R);
		v[x].neg = v[2 * x + 1].neg + v[2 * x + 2].neg;
		v[x].zero = v[2 * x + 1].zero + v[2 * x + 2].zero;
	}
	void update(int x, int L, int R, int pos, int val) {
		//cout<<"L:"<<L<<endl;
		//cout<<"R:"<<R<<endl;
		if (L > pos || R < pos) return;
		if (L == pos && R == pos) {
			if (val == 0)
				v[x].neg = 0, v[x].zero = 1;
			else if (val < 0)
				v[x].neg = 1, v[x].zero = 0;
			else
				v[x].neg = 0, v[x].zero = 0;
			return;
		}
		update(2 * x + 1, L, (L + R) / 2, pos, val);
		update(2 * x + 2, (L + R) / 2 + 1, R, pos, val);
		v[x].neg = v[2 * x + 1].neg + v[2 * x + 2].neg;
		v[x].zero = v[2 * x + 1].zero + v[2 * x + 2].zero;
	}
	pair<int, int> query(int x, int L, int R, int tL, int tR) {
		if (L > tR || R < tL) return make_pair(0,0);
		if (tL <= L && R <= tR) return make_pair(v[x].neg,v[x].zero);
		auto p1 = query(2 * x + 1, L, (L + R) / 2, tL, tR);
		auto p2 =  query(2 * x + 2, (L + R) / 2 + 1, R, tL, tR);
		return  make_pair(p1.first+p2.first,p1.second+p2.second);

	}
};


int main() {
	ios::sync_with_stdio(0);
	int N, K;
	while (cin >> N >> K) {
		vector <int> data(N);
		for (int i = 0; i < N; i++)
			cin >> data[i];
		segtree t(&data);
		while (K--) {
			char c;
			int a, b;
			cin >> c >> a >> b;
			if (c == 'C') {
				a--;
				t.update(0, 0, N - 1, a, b);
			}
			else {
				a--, b--;
				auto tmp = t.query(0, 0, N - 1, a, b);
				if (tmp.second > 0) cout << 0;
				else if (tmp.first %2 == 1) cout << '-';
				else cout << '+';
			}
		}
		cout << endl;
	}
}
