#include <iostream>
#include <vector>
#define INF 1000000000
using namespace std;

class segtree {
private:
	struct node {
		int val = 0, len = 0, status = -1; //nothing  allzero allone
		bool inverse = false;
	};
	vector<int>* data;
	vector<node> v;
public:
	segtree(vector<int>* x) {
		v.resize(1024000 * 3 + 10);
		data = x;
		build (0, 0, data->size() - 1);
	}

	void build(int x, int left, int right) {
		if (left == right) {
			v[x].val = data->at(left);
			v[x].len = 1;
			return;
		}
		build (2 * x + 1, left, (left + right) / 2);
		build (2 * x + 2, (left + right) / 2 + 1, right);
		v[x].val = v[2 * x + 1].val + v[2 * x + 2].val;
		v[x].len = v[2 * x + 1].len + v[2 * x + 2].len;
	}

	void push_down(int x) {
		if (v[x].status == -1 && !v[x].inverse) return;
		else if ((v[x].status == 0 && !v[x].inverse) ||
		         (v[x].status == 1 &&  v[x].inverse)) {
			v[2 * x + 1].status = 0;
			v[2 * x + 2].status = 0;
			v[2 * x + 1].inverse = false;
			v[2 * x + 2].inverse = false;
			v[2 * x + 1].val = 0;
			v[2 * x + 2].val = 0;
			v[x].status = -1;
			v[x].inverse = false;
		}
		else if ((v[x].status == 1 && !v[x].inverse) ||
		         (v[x].status == 0 &&  v[x].inverse)) {
			v[2 * x + 1].status = 1;
			v[2 * x + 2].status = 1;
			v[2 * x + 1].inverse = false;
			v[2 * x + 2].inverse = false;
			v[2 * x + 1].val = v[2 * x + 1].len;
			v[2 * x + 2].val = v[2 * x + 2].len;
			v[x].status = -1;
			v[x].inverse = false;
		}
		else if (v[x].inverse) {
			v[2 * x + 1].val = v[2 * x + 1].len - v[2 * x + 1].val;
			v[2 * x + 2].val = v[2 * x + 2].len - v[2 * x + 2].val;
			v[2 * x + 1].inverse ^= true;
			v[2 * x + 2].inverse ^= true;
			v[x].inverse = false;
		}
		else cout << "Not Suppose to Happen" << endl;
		//print();
	}

	int query(int x, int left, int right, int tleft, int tright) {
		if (tright < left || tleft > right) return 0;
		if (tleft <= left && tright >= right) return v[x].val;
		push_down(x);
		return query(2 * x + 1, left, (left + right) / 2, tleft, tright) +
		       query(2 * x + 2, (left + right) / 2 + 1, right, tleft, tright);
	}

	void update(int x, int left, int right, int tleft, int tright, char type) {
		if (tright < left || tleft > right) return;
		if (tleft <= left && tright >= right) {
			if (type == 'F') {
				v[x].status = 1;
				v[x].val = v[x].len;
				v[x].inverse = false;
			}
			else if (type == 'E') {
				v[x].status = 0;
				v[x].val = 0;
				v[x].inverse = false;
			}
			else {
				v[x].inverse ^= true;
				v[x].val = v[x].len - v[x].val;
			}
			return;
		}
		push_down(x);
		update(2 * x + 1, left, (left + right) / 2, tleft, tright, type);
		update(2 * x + 2, (left + right) / 2 + 1, right, tleft, tright, type);
		v[x].val = v[2 * x + 1].val + v[2 * x + 2].val;
	}

	void print() {
		for (int i = 0; i < data->size() * 2; i++) {
			cout << i << ": " << v[i].val << ' ' << v[i].len << ' ' << v[i].status << ' ' << v[i].inverse << endl;
		}
	}
};

int main() {
	ios::sync_with_stdio(0);
	int T;
	cin >> T;
	for (int t = 1; t <= T; t++) {
		vector<int> data;
		int N;
		cin >> N;
		while (N--) {
			int M;
			char c;
			vector<int> tmp;
			cin >> M;
			cin.ignore();
			while (c = cin.get(), c != '\n')
				tmp.push_back(c - '0');
			while (M--)
				data.insert(data.end(), tmp.begin(), tmp.end());
		}
		segtree magic(&data);
		cin >> N;
		cout << "Case " << t << ":" << endl;
		int q = 1;
		while (N--) {
			//magic.print();
			char c;
			int a, b;
			cin >> c >> a >> b;
			if (c == 'S') {
