            else:
                tmp.append(0)
            if len(bh) > 0:
                tmp.append(heapq.heappop(bh))
            else:
                tmp.append(0)
            bf.append(tmp)

        for i in bf:
            tmp = i[0] - i[1]
            if tmp < 0:
                heapq.heappush(gh, tmp)
            elif tmp > 0:
                heapq.heappush(bh, -tmp)

    if len(gh) == 0 and len(bh) == 0:
        print("green and blue died")
    elif len(gh) == 0:
        print("blue wins")
        while len(bh) > 0:
            print(-heapq.heappop(bh))

