						now +=temp, y+=temp;
						break;
					case 1:
						now +=temp, x-=temp;
						break;
					case 2:
						now +=temp, y-=temp;
						break;
					case 3:
						now +=temp, x+=temp;
				}
			else {
				switch(direction%4){
					case 0:
						for (ll i = 0; i  < length/2 && now != P; i++)
							now++,y++;
						break;
					case 1:
						for (ll i = 0; i  < length/2 && now != P; i++)
							now++,x--;
						break;
					case 2:
						for (ll i = 0; i  < length/2 && now != P; i++)
							now++,y--;
						break;
					case 3:
						for (ll i = 0; i  < length/2 && now != P; i++)
							now++,x++;
						break;

					}
			}
			direction++;
			length++;
		}
		cout<<"Line = "<<y+1<<", column = "<<x+1<<'.'<<endl;

	}


}
