#include <iostream>
#include <vector>
#include <cstring>
#include <algorithm>

using namespace std;

vector <int> v[26],node,path,band,ans;
int visit[26], depth = 1, min_band = 1000, size;

void dfs(int n){
	depth++;
	path.push_back(n);
	visit[n] = depth;

	int temp = 0;
	for (auto i:v[n])
		if (visit[i]) temp = max (temp, depth - visit[i]);

	if (band.empty()) band.push_back(temp);
	else band.push_back(max(temp,band.back()));

	if (band.back() < min_band && depth == size) {
		ans = path;
		min_band = band.back();
	}

	for (auto i:node)
		if (!visit[i]) dfs(i);

	path.pop_back();
	band.pop_back();
	visit[n] = false;
	depth --;
}

int main(int argc, char const *argv[])
{
	while (cin.peek() != '#'){
		memset(visit,0,sizeof(visit));

		char ch,ch1;
		bool flag = false;
		for (auto &i:v)
			i.clear();
		node.clear();
		depth = 1, min_band = 1000, size = 0;

		while (ch = cin.get(), ch != '\n'){
			if (ch == ':') flag = true;
			else if (ch == ';') flag = false;
			else if (!flag) {
				ch1 = ch;
				visit[ch-'A'] = true;
			}
			else if (flag) {
				if (find(v[ch1-'A'].begin(),v[ch1-'A'].end(),ch-'A') == v[ch1-'A'].end())
					v[ch1-'A'].push_back(ch-'A');
				if (find(v[ch-'A'].begin(),v[ch-'A'].end(),ch1-'A') == v[ch-'A'].end())
					v[ch-'A'].push_back(ch1-'A');
				visit[ch-'A'] = true;
			}
		}	

		for (int i = 0; i < 26; i++)
			if (visit[i]) node.push_back(i),size++;
		size++;
		memset(visit,0,sizeof(visit));

		for (auto i:node)
			dfs(i);

		for (auto i:ans)
			cout<<char(i+'A')<<' ';
		cout<<"-> "<<min_band<<endl;
	}
	return 0;
}
```

