{
	int n;
	cin>>n;
	while (n--){
		int a,b;
		cin>>a>>b;
		queue <job> q;
		priority_queue <int> pq;
		for (int i = 0; i < a; ++i){
			int temp;
			cin>>temp;
			pq.push(temp);
			if (i == b)
				q.push(job(temp,1));
			else q.push(job(temp,0));
		}

		int time = 0;
		while (1){
			if (q.front().priority == pq.top()){
				if (q.front().ans == 1){
					time ++;
					cout<<time<<endl;
					break;
				} 
				else {
					pq.pop();
					q.pop();
					time++;
				}
			}
			else {
				q.push(q.front());
				q.pop();
			}
		}

	}
	return 0;
}

