#include <iostream>
#include <string>
using namespace std;

char mirrorChar (char c){
	switch(c){
		case 'A':
			return 'A';
		case 'E':
			return '3';
		case 'H':
			return 'H';
		case 'I':
			return 'I';
		case 'J':
			return 'L';
		case 'L':
			return 'J';
		case 'M':
			return 'M';
		case 'O':
			return 'O';
		case 'S':
			return '2';
		case 'T':
			return 'T';
		case 'U':
			return 'U';
		case 'V':
			return 'V';
		case 'W':
			return 'W';
		case 'X':
			return 'X';
		case 'Y':
			return 'Y';
		case 'Z':
			return '5';
		case '1':
			return '1';
		case '2':
			return 'S';
		case '3':
			return 'E';
		case '5':
			return 'Z';
		case '8':
			return '8';
		default:
			return '-';
		}
}

int main(int argc, char const *argv[])
{
	string s;
	while(cin>>s) {
		int len = s.length();
		bool pal=true, mirror=true;
		for (int i=0;i*2<len;i++){
			if (s[i]!=s[len-i-1]) pal=false;
			if (s[i]!=mirrorChar(s[len-i-1])) mirror = false;
		}
		if (!pal && !mirror)
			cout<<s<<" -- is not a palindrome."<<endl;
		if (!pal && mirror)
			cout<<s<<" -- is a mirrored string."<<endl;
		if (pal && !mirror)
			cout<<s<<" -- is a regular palindrome."<<endl;
		if (pal && mirror)
			cout<<s<<" -- is a mirrored palindrome."<<endl;
		cout<<endl;
	}
	return 0;
}
