#include <iostream>
#include <string>
#include <vector>

using namespace std;

class dice
{
	public:
		dice(int s[]);
		bool operator == (const dice& a);
		void rotate_a();
		void rotate_b();
		void rotate_c();
		bool compare(dice a);
	private:
		int data[6];
};

dice::dice(int s[]){ //do this becasue of reusing code from uva 253
	data[0] = s[0];
	data[1] = s[1];
	data[3] = s[2];
	data[4] = s[5];
	data[2] = s[4];
	data[5] = s[3];
}

bool dice::operator == (const dice& a){
	for (int i = 0; i < 6; ++i)
		if (data[i] != a.data[i])
			return false;
	return true;
}

void dice::rotate_a (){
	int temp = data[2];
	data[2] = data[1];
	data[1] = data[3];
	data[3] = data[4];
	data[4] = temp;
}

void dice::rotate_b (){
	int temp = data[0];
	data[0] = data[1];
	data[1] = data[5];
	data[5] = data[4];
	data[4] = temp; 
}

void dice::rotate_c(){
	int temp = data[0];
	data[0] = data[3];
	data[3] = data[5];
	data[5] = data[2];
	data[2] = temp;
}

bool dice::compare (dice a){
	for (int i = 0; i < 4; ++i)
	{
		for (int i = 0; i < 4; ++i)
		{
			this->rotate_a();
			if (a == *this) return true;
		}
		for (int i = 0; i < 4; ++i)
		{
			this->rotate_b();
			if (a == *this) return true;
		}
		this->rotate_c();
	}
	return false;
}

int main(int argc, char const *argv[])
{
	int n;
	while (cin>>n,n){
		int a[6],cnt=0;
		vector<dice> v1,v2;
		while (n--){
			for (int i = 0; i < 6; ++i)
				cin>>a[i];
			v1.push_back(dice(a));
		}
		for (auto i:v1){
			bool flag = true;
			for (auto j:v2){
				if (i.compare(j)){
					flag = false;
					break;
				}
			}
			if (flag) {
				v2.push_back(i);
				cnt++;
			}
		}
		cout<<cnt<<endl;
	}
	return 0;
}
