}

void dice::rotate_c(){
	int temp = data[0];
	data[0] = data[3];
	data[3] = data[5];
	data[5] = data[2];
	data[2] = temp;
}

bool dice::compare (dice a){
	for (int i = 0; i < 4; ++i)
	{
		for (int i = 0; i < 4; ++i)
		{
			this->rotate_a();
			if (a == *this) return true;
		}
		for (int i = 0; i < 4; ++i)
		{
			this->rotate_b();
			if (a == *this) return true;
		}
		this->rotate_c();
	}
	return false;
}

int main(int argc, char const *argv[])
{
	string s;
	while (cin>>s){ 
		dice a(s.substr(0,6)),b(s.substr(6,12));
		a.compare(b)?cout<<"TRUE"<<endl:cout<<"FALSE"<<endl;
	}
	return 0;
}
