#include <iostream>
#include <sstream>
#include <cstring>
#include <queue>

using namespace std;

class canvas
{
public:
	canvas();
	void resize(int x, int y);
	void print();
	void clear();
	void color(int x, int y, char c);
	void draw_vertical(int x, int y1, int y2, char c);
	void draw_horizontal (int x1, int x2, int y, char c);
	void fill (int x1, int y1, int x2, int y2, char c);
	void fill_region(int x, int y, char c);
	void save(string s);

private:
	int width,height;
	char table[300][300];
	bool travel[300][300];
};

canvas::canvas(){
	clear();
}

void canvas::print(){
	for (int i = 1; i <= height; ++i){
		for (int j = 1; j <= width; ++j)
			cout<<table[i][j];
		cout<<endl;
	}
}

void canvas::clear(){
	memset(table,'O',sizeof(table));
}

void canvas::color(int x, int y, char c){
	table[y][x] = c;
}

void canvas::draw_vertical(int x, int y1, int y2, char c){
	if (y1 > y2) swap(y1,y2);
	for (int i = y1; i <= y2; i++)
		color(x,i,c);
}

void canvas::draw_horizontal(int x1, int x2, int y, char c){
	if (x1 > x2) swap(x1,x2);
	for (int i = x1; i <= x2; i++)
		color(i,y,c);
}

void canvas::fill (int x1, int y1, int x2, int y2, char c){
	for (int i = y1; i <= y2; i++)
		draw_horizontal(x1,x2,i,c);
}

void canvas::save (string s){
	cout<<s<<endl;
	print();
}

void canvas::resize (int x, int y){
	width = x;
	height = y;
	clear();
}

void canvas::fill_region (int x, int y, char c){
	queue <int> qx,qy;
	memset (travel,0,sizeof(travel));
	char org = table[y][x];
	travel[y][x] = true;
	qx.push(x);
	qy.push(y);
	while (!qx.empty()){
		int tempx = qx.front(), tempy = qy.front();
		color(tempx, tempy, c);
		qx.pop();
		qy.pop();
		if (tempy-1 > 0 && !travel[tempy-1][tempx] && table[tempy-1][tempx] == org) {
			qx.push(tempx);
			qy.push(tempy-1);
			travel[tempy-1][tempx] = true;
		}
		if (tempx-1 > 0 && !travel[tempy][tempx-1] && table[tempy][tempx-1] == org) {
			qx.push(tempx-1);
			qy.push(tempy);
			travel[tempy][tempx-1] = true;
		}
		if (tempx+1 <= width > 0 && !travel[tempy][tempx+1] && table[tempy][tempx+1] == org) {
			qx.push(tempx+1);
			qy.push(tempy);
			travel[tempy][tempx+1] = true;
		}
		if (tempy+1 <= height > 0 && !travel[tempy+1][tempx] && table[tempy+1][tempx] == org) {
			qx.push(tempx);
			qy.push(tempy+1);
			travel[tempy+1][tempx] = true;
		}

	}

}

int main(int argc, char const *argv[])
{
	string s;
	canvas can;
	while (getline(cin,s)){
		//cout<<"s: "<<s<<endl;
		char ch,e;
		int a,b,c,d;
		stringstream ss(s);
		ss>>ch;
		switch (ch){
			case 'C':
				can.clear();
				break;
			case 'I':
				ss>>a>>b;
				can.resize(a,b);
				break;
			case 'L':
				ss>>a>>b>>e;
				can.color(a,b,e);
				break;
			case 'V':
				ss>>a>>b>>c>>e;
				can.draw_vertical(a,b,c,e);
				break;
			case 'H':
				ss>>a>>b>>c>>e;
				can.draw_horizontal(a,b,c,e);
				break;
			case 'K':
				ss>>a>>b>>c>>d>>e;
				can.fill(a,b,c,d,e);
				break;
			case 'F':
				ss>>a>>b>>e;
				can.fill_region(a,b,e);
				break;
			case 'S':
				ss>>s;
				can.save(s);
				break;
			case 'X':
				return 0;
			default:
				break;
		}
	}
	return 0;
}
