#include <iostream>
#include <string>
#include <regex>
#include <iomanip>

using namespace std;

double prefix (char c){
	switch (c){
		case 'm':
			return 0.001;
		case 'k':
			return 1000;
		case 'M':
			return 1000000;
		default:
			return 1;
	}
}

int main(int argc, char const *argv[])
{
	int cnt;
	cin>>cnt;
	cin.ignore();
	for (int i = 1;i <= cnt; i++){
		string s,data1,data2,data1_pre,data2_pre,temp,data1_con,data2_con;
		double ddata1,ddata2;
		getline(cin,s);

		smatch sm;
		regex e ("([U|P|I])=([0-9.]*)([m|k|M]?)");
		regex_search (s,sm,e);
		data1_con = sm[1];
		data1 = sm[2];
		data1_pre = sm[3];
		temp = sm.suffix().str(); 
		regex_search (temp,sm,e);
		data2_con = sm[1];
		data2 = sm[2];
		data2_pre = sm[3];

		cout<<"Problem #"<<i<<endl;
		double ans;
		ddata1 = (double)stod(data1)*prefix(data1_pre[0]);
		ddata2 = (double)stod(data2)*prefix(data2_pre[0]);
		if (data1_con[0] != 'P' && data2_con[0] != 'P'){
			ans = ddata1 * ddata2;
			cout<<"P="<<fixed<<setprecision(2)<<ans<<'W'<<endl;
		}
		if (data1_con[0] != 'I' && data2_con[0] != 'I'){
			cout<<"I=";
			if (data1_con[0] == 'P'){
				ans = ddata1 / ddata2;
				cout<<fixed<<setprecision(2)<<ans<<'A'<<endl;
			}
			else{
				ans = ddata2 / ddata1;
				cout<<fixed<<setprecision(2)<<ans<<'A'<<endl;
			}
		}
		if (data1_con[0] != 'U' && data2_con[0] != 'U'){
			cout<<"U=";
			if (data1_con[0] == 'P'){
				ans = ddata1 / ddata2;
				cout<<fixed<<setprecision(2)<<ans<<'V'<<endl;
			}
			else{
				ans = ddata2 / ddata1;
				cout<<fixed<<setprecision(2)<<ans<<'V'<<endl;
			}
		}
		cout<<endl;
	}
	return 0;
}
